import React from "react";
import logo from "../assets/polilogo.png"; // 로고 이미지 import
import arrow1 from "../assets/arrow-1.svg";
import arrow2 from "../assets/arrow-1-2.svg";

const Desktop = () => {
  return (
    <div className="desktop">
      {/* Header Section */}
      <div className="header">
        <div className="logo">
          <img src={logo} alt="POLITRACKER 로고" className="logo-img" />
        </div>
        <p className="slogan">
          객관적 정보, 주체적 판단
          <br />
          청년을 위한 정치 플랫폼
        </p>
      </div>

      {/* Intro Section */}
      <div className="intro">
        <p className="intro-text">
          정치가 어렵게 느껴지시나요?
          <br />
          폴리트래커로 중요한 사안과 정보를 한눈에, 쉽게 확인하세요!
        </p>
      </div>

      {/* Cards Section */}
      <div className="cards">
        {/* Card 1: Political Orientation */}
        <Card
          title1="나의 정치 성향"
          title2="분석하기"
          linkText="Learn more"
          arrowSrc={arrow1}
          bgColor="dark"
        />

        {/* Card 2: Local Politicians */}
        <Card
          title1="우리 지역 정치인"
          title2="트래킹하기"
          linkText="Learn more"
          arrowSrc={arrow2}
          bgColor="light"
        />

        {/* Card 3: Political Terminology */}
        <Card
          title1="낯선 정치 용어"
          title2="알아보기"
          linkText="Learn more"
          arrowSrc={arrow1}
          bgColor="purple"
        />
      </div>

      {/* Footer Section */}
      <footer className="footer">
        <p className="footer-text">
          성균관대학교 트래커스꾸
          <br />
          서울특별시 종로구 성균관로 25-2
          <br />
          trackerskku@g.skku.edu
        </p>
      </footer>
    </div>
  );
};

const Card = ({ title1, title2, linkText, arrowSrc, bgColor }) => {
  const cardClass = `card ${bgColor}`;

  return (
    <div className={cardClass}>
      <div className="card-content">
        <div className="card-heading">
          <div className="card-title">{title1}</div>
          <div className="card-subtitle">{title2}</div>
        </div>
        <div className="card-link">
          <div className="card-icon">
            <img className="arrow" src={arrowSrc} alt="Arrow Icon" />
          </div>
          <span className="link-text">{linkText}</span>
        </div>
      </div>
    </div>
  );
};

export default Desktop;
